import { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

import axios from '../axios';

import { Box, Button, Container, Grid, TextField } from '@mui/material';
import { ImageAspectRatioRounded } from '@mui/icons-material';

const AuthorAccount = () => {
  const navigate = useNavigate();

  const [editing, setEditing] = useState(false);
  const [data, setData] = useState(null);

  const firstNameRef = useRef(null);
  const lastNameRef = useRef(null);
  const avatarRef = useRef(null);

  const fetchData = async () => {
    // You can await here
    try {
      const response = await axios.get('author/');
      setData(() => ({ ...response.data }));
    } catch (error) {
      console.log(error);
      alert(error.response);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleSubmit = async () => {
    const data = {
      last_name: lastNameRef.current.value,
      first_name: firstNameRef.current.value,
      avatar: avatarRef?.current?.value,
    };
    try {
      const response = await axios.patch('author/', data);
      console.log(data);
      alert('updated');
      navigate('/author', { replace: true });
    } catch (err) {
      console.log(err);
      if (!err.response) {
        alert('No Server Response');
      } else {
        const errorMessage = JSON.stringify(err.response.data).replace(/[{"}[\]]/g, ' ');
        alert(errorMessage);
      }
    }
  };

  if (!data) {
    return <h1>Loading ...</h1>;
  }

  return (
    <Container component='main' maxWidth='xs'>
      <Box
        component='form'
        noValidate
        onSubmit={handleSubmit}
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <img src={data.avatar} alt='avatar' />
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              id='firstName'
              name='firstName'
              autoComplete='firstName'
              ref={firstNameRef}
              defaultValue={data.first_name}
              label={editing ? 'First name' : ''}
              variant={editing ? 'outlined' : 'standard'}
              disabled={!editing}
            ></TextField>
          </Grid>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              id='lastName'
              name='lastName'
              autoComplete='lastName'
              ref={lastNameRef}
              label={editing ? 'Last name' : ''}
              variant={editing ? 'outlined' : 'standard'}
              defaultValue={data.last_name}
            ></TextField>
          </Grid>
        </Grid>

        <Button
          type={editing ? 'submit' : 'button'}
          fullWidth
          variant='contained'
          sx={{ mt: 3, mb: 2 }}
          onClick={() => setEditing(true)}
        >
          Edit
        </Button>
      </Box>
    </Container>
  );
};

export default AuthorAccount;
