import * as React from 'react';
import PropTypes from 'prop-types';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Link from '@mui/material/Link';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import { ShoppingCart } from '@mui/icons-material';

function CounrseCard({ image, title, description, link }) {
  const { post } = props;

  return (
    <Paper
      sx={{
        position: 'relative',
        backgroundColor: 'grey.800',
        color: '#fff',
        mb: 4,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        backgroundImage: `url(${image})`,
        height: '500px',
      }}
    >
      {/* Increase the priority of the hero background image */}
      {<img style={{ display: 'none' }} src={image} />}
      <Box
        sx={{
          position: 'absolute',
          top: 0,
          bottom: 0,
          right: 0,
          left: 0,
          backgroundColor: 'rgba(0,0,0,.3)',
        }}
      />
      <Grid container>
        <Grid item md={6}>
          <Box
            sx={{
              position: 'relative',
              p: { xs: 3, md: 6 },
              pr: { md: 0 },
            }}
          >
            <Typography component='h1' variant='h3' color='inherit' gutterBottom>
              {title}
            </Typography>
            <Typography variant='h5' color='inherit' paragraph>
              {description}
            </Typography>
            <Link variant='subtitle1' href='#'>
              {linkText}
            </Link>
            <br />
            <Button variant='contained' color='success' sx={{ marginTop: '1rem' }} startIcon={<ShoppingCart />}>
              Enroll Now!
            </Button>
          </Box>
        </Grid>
      </Grid>
    </Paper>
  );
}

CounrseCard.propTypes = {
  post: PropTypes.shape({
    description: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
    imageText: PropTypes.string.isRequired,
    linkText: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
  }).isRequired,
};

export default CounrseCard;
