import React from 'react';
import Grid from '@mui/material/Unstable_Grid2';
import Typography from '@mui/material/Typography';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import {
  CardActionArea,
  Button,
  TextField,
  ButtonGroup,
  ListItem,
  ListItemAvatar,
  Avatar,
  ListItemText,
  Rating,
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

import { useNavigate } from 'react-router-dom';
import Footer from './footer';

const CourseCard = ({ image, author, authorIcon, title, rating }) => {
  const navigate = useNavigate();
  return (
    <Card sx={{ maxWidth: 345, borderRadius: '1rem' }} onClick={() => navigate('course')}>
      <CardActionArea>
        <CardMedia component='img' image={image} sx={{ borderRadius: '2rem', padding: '1rem' }} />
        <CardContent sx={{ padding: '0.7rem' }}>
          <ListItem button>
            <ListItemAvatar>
              <Avatar alt='Profile Picture' src={authorIcon} />
            </ListItemAvatar>
            <ListItemText sx={{ color: 'grey' }} primary={author} />
          </ListItem>
          <Typography>{title}</Typography>
          <Grid container spacing={'auto'}>
            <Rating size='small' value={rating} readOnly />
            <Grid xs>
              <Typography align='right' component='legend'>
                Rating
              </Typography>
            </Grid>
          </Grid>
        </CardContent>
      </CardActionArea>
    </Card>
  );
};

const Home = () => {
  const navigate = useNavigate();
  return (
    <>
      <Grid container justifyContent={'center'} mt={2}>
        <Grid xs={8}>
          <TextField
            id='1'
            label=''
            variant='outlined'
            color='primary'
            size='small'
            // value={}
            // onChange={}
            sx={{ width: '100%' }}
          />
        </Grid>
        <Grid sm={1}>
          <Button color='primary' aria-label='upload picture' component='label' variant='outlined' size='large'>
            <SearchIcon />
          </Button>
        </Grid>
      </Grid>
      <Grid
        container
        spacing={{ xs: 2, md: 4, lg: 5 }}
        // columns={{ xs: 4, sm: 8, md: 12 }}
        style={{ margin: '1rem' }}
      >
        {courses.map((data, index) => (
          <Grid xs={12} sm={6} md={4} key={index}>
            <CourseCard {...data} />
          </Grid>
        ))}
      </Grid>
      <ButtonGroup variant='outlined' color='primary' aria-label='' sx={{ display: 'flex', justifyContent: 'center' }}>
        <Button>1</Button>
        <Button>2</Button>
      </ButtonGroup>
      <Footer />
    </>
  );
};
export default Home;

const courses = [
  {
    author: 'Jason Willams',
    title: 'Data Science and Machine Learning with Python -Hands On!',
    image: 'images/courses-01.jpg',
    authorIcon: 'images/author-01.jpg',
    rating: 4,
  },
];
