import { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { GoogleMap, Marker, Autocomplete } from '@react-google-maps/api';
import axios from '../../../api/axios';
import ServiceList from '../services/list';

const AuthorAccount = () => {
  const navigate = useNavigate();

  const [data, setData] = useState(null);

  const firstNameRef = useRef(null);
  const lastNameRef = useRef(null);
  const avatarRef = useRef(null);

  const fetchData = async () => {
    // You can await here
    try {
      const response = await axios.get('author/');
      setData(() => ({ ...response.data }));
    } catch (error) {
      console.log(error);
      alert(error.response);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleSubmit = async () => {
    const data = {
      last_name: lastNameRef.current.value,
      first_name: firstNameRef.current.value,
      avatar: avatarRef?.current?.value,
    };
    try {
      const response = await axios.patch('author/', data);
      console.log(data);
      alert('updated');
      navigate('/author', { replace: true });
    } catch (err) {
      console.log(err);
      if (!err.response) {
        alert('No Server Response');
      } else {
        const errorMessage = JSON.stringify(err.response.data).replace(/[{"}[\]]/g, ' ');
        alert(errorMessage);
      }
    }
  };

  if (!data) {
    return <h1>Loading ...</h1>;
  }

  return (
    <Container component='main' maxWidth='xs'>
      <Box component='form' noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              id='firstName'
              name='firstName'
              autoComplete='firstName'
              ref={firstNameRef}
              value={data.first_name}
            >
              {editing && <InputLabel shrink>First Name</InputLabel>}
            </TextField>

            <TextField
              required
              fullWidth
              id='lastName'
              name='lastName'
              autoComplete='lastName'
              ref={lastNameRef}
              value={data.last_name}
            >
              {editing && <InputLabel shrink>Last Name</InputLabel>}
            </TextField>

            <TextField
              required
              fullWidth
              id='lastName'
              name='lastName'
              autoComplete='lastName'
              ref={lastNameRef}
              value={data.last_name}
            >
              {editing && <InputLabel shrink>Last Name</InputLabel>}
            </TextField>
          </Grid>
        </Grid>
        <Button type='submit' fullWidth variant='contained' sx={{ mt: 3, mb: 2 }}>
          Sign Up
        </Button>
        <Grid container justifyContent='flex-end'>
          <Grid item>
            <Link href='signin' variant='body2'>
              Already have an account? Sign in
            </Link>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
};

export default AuthorAccount;
