import {
  Avatar,
  Box,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  Fade,
  Grid,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Rating,
  Typography,
  useScrollTrigger,
} from '@mui/material';
import theme from './theme';
import { useNavigate } from 'react-router-dom';

export const CourseCard = ({ image, author, authorIcon, title, rating }) => {
  const navigate = useNavigate();
  return (
    <Card
      variant='outlined'
      sx={{ borderRadius: '1rem', borderColor: theme.palette.primary.light, maxWidth: 450, margin: 'auto' }}
      onClick={() => navigate('course')}
    >
      <CardActionArea>
        <CardMedia component='img' image={image} sx={{ borderRadius: '1.5rem', padding: '.7rem' }} />
        <CardContent sx={{ padding: '0.7rem' }}>
          <ListItem button sx={{ paddingX: 0 }}>
            <ListItemAvatar>
              <Avatar alt='Profile Picture' src={authorIcon} />
            </ListItemAvatar>
            <ListItemText sx={{ color: 'grey' }} primary={author} />
          </ListItem>
          <Typography>{title}</Typography>
          <Grid container alignItems='center'>
            <Grid item xs>
              <Typography align='right' component='legend'>
                {rating}
                <Rating size='small' value={rating} sx={{ padding: 0 }} readOnly />
              </Typography>
            </Grid>
          </Grid>
        </CardContent>
      </CardActionArea>
    </Card>
  );
};

export function ScrollTop(props) {
  const { children } = props;
  // Note that you normally won't need to set the window ref as useScrollTrigger
  // will default to window.
  // This is only being set here because the demo is in an iframe.
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 100,
  });

  const handleClick = (event) => {
    const anchor = (event.target.ownerDocument || document).querySelector('#root');

    if (anchor) {
      anchor.scrollIntoView({
        block: 'center',
      });
    }
  };

  return (
    <Fade in={trigger}>
      <Box onClick={handleClick} role='presentation' sx={{ position: 'fixed', bottom: 16, right: 16 }}>
        {children}
      </Box>
    </Fade>
  );
}
